import React, { useState, useEffect } from 'react';
import './styles.css'; // Import the CSS file

const Profile = ({ userID, role }) => {
  const [profile, setProfile] = useState(null);
  const [basicName, setBasicName] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    console.log("Role: ", role);
    console.log("UserID: ", userID);
    // Fetch the basic name first (from /getName route)
    const fetchBasicName = async () => {
      try {
        const response = await fetch(`http://localhost:5000/getName/${role}/${userID}`);
        const data = await response.json();

        if (response.ok) {
          setBasicName(data.name); // Store name
        } else {
          console.error(data.message); // Handle errors
        }
      } catch (error) {
        console.error('Error fetching name:', error);
      }
    };

    // Fetch full profile data (from /getProfile route)
    const fetchProfile = async () => {
      try {
        const response = await fetch(`http://localhost:5000/getProfile/${role}/${userID}`);
        const data = await response.json();

        if (response.ok) {
          setProfile(data); // Store full profile data
        } else {
          console.error(data.message); // Handle errors like "Profile not found"
        }
      } catch (error) {
        console.error('Error fetching profile:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchBasicName();
    fetchProfile();
  }, [userID, role]);

  if (loading) {
    return <div>Loading...</div>; // Show loading message while data is being fetched
  }

  if (!profile) {
    return <div>Profile not found.</div>; // Show error if profile is not found
  }

  return (
    <div className="profile-container">
      <div className="main-content">
        <div className="profile-card">
          <div className="profile-info">
            <div
              className="profile-image"
              style={{
                backgroundImage: `url(/images/${profile.StudentPicture || profile.StaffPicture || 'default.png'})`,
              }}
            ></div>
            <div className="profile-details">
              <div className="profile-id">{profile.StudentID || profile.StaffID}</div>
              <div className="profile-name">{basicName}</div> {/* Use the basic name fetched */}
              <div className="contact-info">
                <div className="contact-item">
                  <span className="contact-label">Phone No.</span>
                  <span className="contact-value">{profile.StudentPhoneNumber || profile.StaffPhoneNumber}</span>
                </div>
                <div className="contact-item">
                  <span className="contact-label">Email</span>
                  <span className="contact-value">{profile.StudentEmail || profile.StaffEmail}</span>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Profile;
