import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './styles.css';

const Profile = ({ staffId }) => {
  const [staff, setStaff] = useState(null);

  useEffect(() => {
    // Fetch staff data from the backend
    axios
      .get(`http://localhost:5000/api/staff/${staffId}`)
      .then((response) => {
        setStaff(response.data);
      })
      .catch((error) => {
        console.error('Error fetching staff data:', error);
      });
  }, [staffId]);

  if (!staff) {
    return <div>Loading...</div>; // Display loading indicator until data is fetched
  }

  return (
    <div className="profile-container">
      <div className="main-content">
        <div className="profile-card">
          <div className="profile-info">
            <div
              className="profile-image"
              style={{
                backgroundImage: `url(http://localhost:5000/uploads/${staff.StaffPicture})`,
              }}
            ></div>
            <div className="profile-details">
              <div className="profile-id">({staff.StaffID})</div>
              <div className="profile-name">{staff.StaffName}</div>
              <div className="contact-info">
                <div className="contact-item">
                  <span className="contact-label">Phone No.</span>
                  <span className="contact-value">{staff.StaffPhoneNumber}</span>
                </div>
                <div className="contact-item">
                  <span className="contact-label">Email</span>
                  <span className="contact-value">{staff.StaffEmail}</span>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Profile;
