import React, { useState, useEffect } from 'react';
import './styles.css';

const Profile = ({ role, id }) => {
  const [profile, setProfile] = useState(null);

  useEffect(() => {
    // Construct the correct API URL based on the role
    const url = `http://localhost:5000/api/profile/${role}/${id}`;

    // Fetch data from the backend
    fetch(url)
      .then((response) => {
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        return response.json();
      })
      .then((data) => {
        setProfile(data);
      })
      .catch((error) => {
        console.error('Error fetching profile data:', error);
      });
  }, [role, id]);

  if (!profile) {
    return <div>Loading...</div>; // Display loading indicator until data is fetched
  }

  // Conditionally render based on role
  const isStudent = role === 'student';

  return (
    <div className="profile-container">
      <div className="main-content">
        <div className="profile-card">
          <div className="profile-info">
            <div
              className="profile-image"
              style={{
                backgroundImage: `url(http://localhost:5000/uploads/${profile.picture})`,
              }}
            ></div>
            <div className="profile-details">
              <div className="profile-id">({profile.id})</div>
              <div className="profile-name">{profile.name}</div>
              <div className="contact-info">
                <div className="contact-item">
                  <span className="contact-label">Phone No.</span>
                  <span className="contact-value">{profile.phone}</span>
                </div>
                <div className="contact-item">
                  <span className="contact-label">Email</span>
                  <span className="contact-value">{profile.email}</span>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Profile;
