import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import './CourtDetail.css';

const CourtDetail = () => {
  const { id } = useParams(); // Get the CourtID from the URL
  const [court, setCourt] = useState(null); // State to store court data
  const [loading, setLoading] = useState(true); // State for loading status

  useEffect(() => {
    // Use the native fetch API to get court details
    fetch(`http://localhost:5000/api/courts/${id}`)
      .then((response) => {
        if (!response.ok) {
          throw new Error('Failed to fetch court details');
        }
        return response.json();
      })
      .then((data) => {
        setCourt(data); // Set the court data
        setLoading(false); // Stop loading
      })
      .catch((error) => {
        console.error('Error fetching court details:', error.message);
        setLoading(false); // Stop loading even if there is an error
      });
  }, [id]);

  if (loading) {
    return <div>Loading...</div>;
  }

  if (!court) {
    return <div>Court not found.</div>;
  }

  return (
    <div className="court-detail-container">
      <header className="detail-header">
        <h1>Venue Details</h1>
      </header>
      <div className="detail-card">
        <h2>NAME</h2>
        <h2>{court.CourtName}</h2>
        <h2>PHOTO</h2>
        <img
          src={`http://localhost:5000/images/${court.CourtPic}`}
          alt={court.CourtName}
          className="detail-image"
        />
        <p>Location: {court.CourtLocation}</p>
      </div>
    </div>
  );
};

// Get references to the buttons
const editButton = document.querySelector('.edit-btn');
const deleteButton = document.querySelector('.delete-btn');

// Edit button functionality
editButton.addEventListener('click', () => {
  alert('Edit functionality to be implemented!');
  // Add your code for editing here
});

// Delete button functionality
deleteButton.addEventListener('click', () => {
  const confirmation = confirm('Are you sure you want to delete this court?');
  if (confirmation) {
    alert('Court deleted successfully!');
    // Add your code for deleting here
  }
});

export default CourtDetail;
