import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import './CourtDetail.css';

const CourtDetail = () => {
  const { id } = useParams(); // Get the CourtID from the URL
  const [court, setCourt] = useState(null); // State to store court data
  const [loading, setLoading] = useState(true); // State for loading status

  useEffect(() => {
    // Fetch court data from the backend
    axios.get(`http://localhost:5000/api/courts/${id}`)
      .then((response) => {
        setCourt(response.data); // Set the court data
        setLoading(false); // Stop loading
      })
      .catch((error) => {
        console.error('Error fetching court details:', error.message);
        setLoading(false); // Stop loading even if there is an error
      });
  }, [id]);

  if (loading) {
    return <div>Loading...</div>;
  }

  if (!court) {
    return <div>Court not found.</div>;
  }

  return (
    <div className="court-detail-container">
      <header className="detail-header">
        <h1>Venue Details</h1>
      </header>
      <div className="detail-card">
        <div className="detail-info">
          <h2>Name</h2>
          <p>{court.CourtName}</p>
          <h2>Photo</h2>
          <img
            src={`http://localhost:5000/images/${court.CourtPic}`} // Adjust path if serving static images
            alt={court.CourtName}
            className="detail-image"
          />
          <h2>Location</h2>
          <p>{court.CourtLocation}</p>
        </div>
      </div>
    </div>
  );
};

export default CourtDetail;
