import React, { useEffect, useState, useContext } from 'react';
import { UserContext } from './UserContext'; // Adjust the path if necessary
import './Hero.css';

const Hero = () => {
  const { user } = useContext(UserContext); // Access user from context
  const [staffName, setStaffName] = useState(''); // State to store the staff's name

  useEffect(() => {
    if (user && user.UserID) {
      // Make API call to fetch staff's name based on user.UserID
      fetch(`http://localhost:5000/getStaffName/${user.UserID}`)
        .then((response) => response.json())
        .then((data) => {
          if (data.staffName) {
            setStaffName(data.staffName); // Set the staff's name in the state
          } else {
            console.error('Staff name not found:', data);
          }
        })
        .catch((error) => {
          console.error('Error fetching staff name:', error);
        });
    }
  }, [user]);

  return (
    <div className="hero">
      <h1>WELCOME TO SUKAN UKM</h1>
      <h2>{staffName || 'Loading...'}</h2> {/* Display staff's name or 'Loading...' */}
    </div>
  );
};

export default Hero;
