import React, { useEffect, useState, useContext } from 'react';
import { UserContext } from './UserContext';  // Adjust the path if necessary
import './Hero.css';

const Hero = () => {
  const { user } = useContext(UserContext); // Access user from context
  const [staffName, setStaffName] = useState('');

  useEffect(() => {
    if (user && user.UserID) {
      // Make API call to fetch student's name based on user.UserID
      fetch(`http://localhost:5000/getStaffName/${user.UserID}`)
        .then((response) => response.json())
        .then((data) => {
          if (data.name) {
            setStaffName(data.name);
          }
        })
        .catch((error) => {
          console.error('Error fetching student name:', error);
        });
    }
  }, [user]);

  return (
    <div className="hero-student">
      <h1>WELCOME TO SUKAN UKM</h1>
      <h2>{staffName || 'Loading...'}</h2>
    </div>
  );
};

export default Hero;
