import React, { useEffect, useState } from 'react';
import './HeroStudent.css';

const HeroStudent = ({ userId }) => {
  const [studentName, setStudentName] = useState('');

  useEffect(() => {
    // Fetch the student's name from the backend
    const fetchStudentName = async () => {
      try {
        //const response = await fetch(`http://localhost:5000/student/${userId}`);
        const response = await fetch(`http://localhost:5000/student/A123456`);
        if (response.ok) {
          const data = await response.json();
          setStudentName(data.name); // Ensure 'data.name' matches the backend response
        } else {
          console.error('Failed to fetch student name');
        }
      } catch (error) {
        console.error('Error:', error);
      }
    };

    if (userId) {
      fetchStudentName();
    }
  }, [userId]);

  return (
    <div className="hero-student">
      <h1>WELCOME TO SUKAN UKM</h1>
      <h2>{studentName || 'Loading...'}</h2>
    </div>
  );
};

export default HeroStudent;
